import './App.css';
import React, { useState } from 'react';

function App() {
  const [question, setQuestion] = useState('');
  const [assistantResponses, setAssistantResponses] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Update the list of responses with the new question
      setAssistantResponses(prevResponses => [
        ...prevResponses,
        { role: 'user', content: question },
      ]);

      const response = await fetch('http://localhost:8080/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: question,
      });

      if (!response.ok) {
        throw new Error(`Server responded with ${response.status}`);
      }

      const data = await response.text();

      // Update the list of responses with the assistant's response
      setAssistantResponses(prevResponses => [
        ...prevResponses,
        { role: 'assistant', content: data },
      ]);

      // Clear the input field
      setQuestion('');
    } catch (error) {
      console.error('Error:', error);
    }
  };
  //here is convert to data as a good condition. 
   const content=(data)=>{ 
          // Paragraf içeriğini satırlara ayır
  const lines = data.split(':');

  // Yeni bir dizi oluştur ve satırları düzenle
  const formattedLines = lines.map((line, index, array) => {
    // Boş satırları filtrele
    if (line.trim() !== '') {
      // Satırın sonunda bir numara varsa
      const match = line.match(/(\d+)\.\s*$/);
      if (match) {
        return line.replace(match[0], `:\n${match[1]}.`);
      }
    }
    return line;
  });

  // Satırları birleştir
  const formattedContent = formattedLines.join('\n');

  return formattedContent;    
   }


  return (
    <div className="container mt-5">
      <form onSubmit={handleSubmit}>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            name="question"
            placeholder="Ask a question"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            aria-describedby="button-addon2"
          />
          <div className="input-group-append">
            <button className="btn btn-outline-secondary" type="submit">
              Ask
            </button>
          </div>
        </div>
      </form>

      <div className="mt-3">
        {assistantResponses.map((response, index) => (
          <p key={index}>{response.role}: {content(response.content)}</p>
        ))}
      </div>

    </div>
  );
}

export default App;
