import React, { useState } from 'react';
import './App.css';

function App() {
  const [question, setQuestion] = useState('');
  const [assistantResponses, setAssistantResponses] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    setLoading(true);

    try {
      const response = await fetch('http://localhost:8080/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: question,
      });

      if (!response.ok) {
        throw new Error(`Server responded with ${response.status}`);
      }

      const data = await response.text();

      setAssistantResponses((prevResponses) => [
        ...prevResponses,
        { role: 'assistant', content: data },
      ]);

      // Clear the input field
      setQuestion('');
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className='App'>
        <div className="container" style={{padding:'5rem'}}>
        <form onSubmit={handleSubmit}>
          <h1>fittinggg</h1>
          <div className="input-group mb-2">
            <input
              type="text"
              className="form-control"
              name="question"
              placeholder="Ask a question"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              aria-describedby="button-addon2"
            />
            <div className="input-group-append">
              <button className="btn btn-outline-secondary" type="submit">
                Ask
              </button>
            </div>
          </div>
        </form>

        <div className="mt-3" style={{fontSize:'19px'}}>
          {loading ? (
            <p>Loading...</p>
          ) : (
            assistantResponses.map((response, index) => (
              <p key={index}>{response.role}: {response.content}</p>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
