import './App.css';
import React, { useState } from 'react';

function App() {
  const [question, setQuestion] = useState('');
  const [assistantResponses, setAssistantResponses] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Update the list of responses with the new question
      setAssistantResponses(prevResponses => [
        ...prevResponses,
        { role: 'user', content: question },
      ]);

      const response = await fetch('http://localhost:8080/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: question,
      });

      if (!response.ok) {
        throw new Error(`Server responded with ${response.status}`);
      }

      const data = await response.text();

      // Update the list of responses with the assistant's response
      setAssistantResponses(prevResponses => [
        ...prevResponses,
        { role: 'assistant', content: data },
      ]);

      // Clear the input field
      setQuestion('');
    } catch (error) {
      console.error('Error:', error);
    }
  };



  return (
    <div className='App'>
  <div className="container mt-5" style={{ boxSizing:'border-box',marginTop: '5rem' }}>
      <form onSubmit={handleSubmit}>
      <h1>fittinggg</h1>
        <div className="input-group mb-2">

          <input
            type="text"
            className="form-control"
            name="question"
            placeholder="Ask a question"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            aria-describedby="button-addon2"
          />
          <div className="input-group-append">
            <button className="btn btn-outline-secondary" type="submit">
              Ask
            </button>
          </div>
        </div>
      </form>

      <div className="mt-3" style={{color:'black', fontSize:'18px'}}>
        {assistantResponses.map((response, index) => (
          <p key={index}>{response.role}: {response.content}</p>
        ))}
      </div>

    </div>
    </div>
  );
}

export default App;
